export default function WeaponsReducer(state = {
    cards: [],
    sources: {
        Raid: true,
        Dungeon: true,
        PvP: true
    },
    loading: false,
    role: "Role"
}, action) {
    switch (action.type) {
        case 'LOADING_WEAPONS':
            return {
                ...state,
                loading: true
            };

        case 'ADD_WEAPONS':
            return {
                ...state,
                cards: action.cards,
                loading: false
            };

        case 'DISPLAY_WEAPON':
            return {
                ...state,
                piece: action.piece,
                loading: false
            };

        case 'SET_ROLE':
            return {
                ...state,
                role: action.role
            };

        case 'SET_SOURCE':
            state.sources[action.source] = !action.value;
            return {
                ...state,
                sources: { ...state.sources }
            };

        default:
            return state
    }
}
