import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchCards } from "../actions/itemActions";
import { NavLink } from 'react-router-dom';

import AzeriteCard from '../components/Azerite/AzeriteCard'

import CardColumns from 'react-bootstrap/CardColumns'

class ItemsContainer extends Component {

    state = {
        cards: {
            azerite: [],
            weapons: [],
            trinkets: []
        }
    };

    componentDidMount() {
        this.props.fetchCards(this.props.path)
    }

    displayCards = () => {
        if(this.props.cards[this.props.path].length > 0) {
            return (this.props.cards[this.props.path].map(card => (
                <NavLink style={{ color: "black" }} to={`/${this.props.path}/${card.id}`} key={card.id}><AzeriteCard view={this.props.displayAzerite} info={card} /></NavLink>
            )))
        }
        else {
            return (<div>No Items</div>)
        }
    };

    render() {
        return (
            <CardColumns>
                {this.displayCards()}
            </CardColumns>
        )
    }
}

const mapStateToProps = state => {
    return {
        cards: state.cards,
        loading: state.loading
    }
};

const mapDispatchToProps = dispatch => {
    return {
        fetchCards: path => dispatch(fetchCards(path)),
        displayAzerite: id => dispatch({ type: 'DISPLAY_AZERITE', id })
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(ItemsContainer)
