import React from 'react';
import { connect } from 'react-redux';

const AzeriteDisplay = props => {
    const info = props.info;

    const displayPiece = () => {
        if (info) {
            return (
                <div>
                    <h1>{info.name}</h1>
                    <h3>{info.slot}</h3>
                    <h3>Source: {info.source}</h3>
                    <h4>Tier 4</h4>
                    {info.tier_four.map(trait => (
                        <p style={{ marginBottom: 0 }} key={trait}>{trait}</p>
                    ))}
                    <h4>Tier 1</h4>
                    {info.tier_three.map(trait => (
                        <p style={{ marginBottom: 0 }} key={trait}>{trait}</p>
                    ))}
                    <h4>Tier 2</h4>
                    {info.tier_two.map(trait => (
                        <p style={{ marginBottom: 0 }} key={trait}>{trait}</p>
                    ))}
                    <h4>Tier 1</h4>
                    {info.tier_one.map(trait => (
                        <p style={{ marginBottom: 0 }} key={trait}>{trait}</p>
                    ))}

                    <h2>Views: {info.views}</h2>
                </div>
            )
        }
        else {
            return <div>Loading...</div>
        }
    };

    return (
        <div>
            {displayPiece()}
        </div>
    )
};

const mapStateToProps = state => {
    return {
        info: state.azerite.piece
    }
};

export default connect(mapStateToProps)(AzeriteDisplay);
