export const fetchCards = path => {
    path = path.toUpperCase()
    return (dispatch) => {
        dispatch({ type: `LOADING_${path}`});
        fetch(`/api/${path}`)
            .then(res => res.json())
            .then(responseJSON => { dispatch({ type: `ADD_${path}`, cards: responseJSON})
            })
    }
}
