import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchAzerite } from "../actions/azeriteActions";

import AzeriteCard from '../components/Azerite/AzeriteCard'

import Container from 'react-bootstrap/Container'
import CardColumns from 'react-bootstrap/CardColumns'

import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import AzeriteDisplay from "../components/Azerite/AzeriteDisplay";

class ItemsContainer extends Component {

    state = {
        cards: []
    };

    componentDidMount() {
        this.props.fetchAzerite()
    }

    displayCards = () => {
        if(this.props.cards.length > 0) {
            return (this.props.cards.map(card => (
                <AzeriteCard key={card.id} info={card} />
            )))
        }
        else {
            return (<div>No Items</div>)
        }
    };

    render() {
        return (
            <div>
                <Router>
                    <Switch>
                        <Route path="/azerite/:id">
                            <AzeriteDisplay />
                        </Route>
                    </Switch>
                </Router>
                <Container>
                    <CardColumns>
                        {this.displayCards()}
                    </CardColumns>
                </Container>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        cards: state.cards,
        loading: state.loading
    }
};

const mapDispatchToProps = dispatch => {
    return {
        fetchAzerite: () => dispatch(fetchAzerite())
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(ItemsContainer)
