import React from 'react';
import NavButtonsContainer from './containers/NavButtonsContainer';
import AzeriteDisplay from "./components/Azerite/AzeriteDisplay";
import ItemsContainer from "./containers/ItemsContainer";
import SearchBar from "./components/SearchBar";
import FilterAzerite from "./components/Azerite/FilterAzerite";

import { connect } from 'react-redux'

import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

import 'bootstrap/dist/css/bootstrap.min.css';
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row"
import Col from "react-bootstrap/Col"


const App = props => {
  return (
      <Container>
          <Router>
              <Switch>
                  <Route path="/azerite/:id">
                      <AzeriteDisplay cards={props.cards} />
                  </Route>

                  <Route path="/azerite">
                      <Row>
                          <Col><SearchBar /></Col>
                          <Col><FilterAzerite /></Col>
                      </Row>
                      <Row>
                          <NavButtonsContainer />
                      </Row>
                      <Row>
                          <ItemsContainer />
                      </Row>
                  </Route>

                  <Route path="/">
                      <NavButtonsContainer />
                  </Route>
              </Switch>
          </Router>
      </Container>
  );
}

const mapStateToProps = state => {
    return {
        cards: state.cards
    }
};

export default connect(mapStateToProps)(App);
